import React, { useEffect, useState } from "react";
import "./mapstyle.css";
import axios from "axios";
import { FaCartArrowDown } from "react-icons/fa";
import { CiShoppingCart } from "react-icons/ci";

function Mappage() {
  const [data, setdata] = useState([]);
  const[cartstate,setcartstate]=useState(false)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("https://fakestoreapi.com/products");
        setdata(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // console.log(data);
  console.log("initial"+cartstate);
  const addtocartt =()=>{
    setcartstate(!cartstate)
    console.log(cartstate);
  }
  return (
    <div className="main-container">
      <div className="container">
        <div className="text">
          <h2>Check out our products</h2>
        </div>
        <div className="card-map">
         {data.map((obj=><>
         <div className="card-container">
          <div className="top"><img src={obj.image} alt="" /></div>
          {cartstate ? <h1>added to cart</h1>: <><div className="bottom">
            
            <div className="left">
              <div className="contents">
                <h2>{obj.category}</h2>
                <h3>{obj.price}</h3>
              </div>
            </div>
            <div className="right">
              <button onClick={addtocartt}><FaCartArrowDown  className="cartbutton"/></button>
            </div>
          </div></>}
        
         </div>
         
         
         </>))}
         
        </div>

        <div className="cart-icon">
        <CiShoppingCart  className="pinkcart"/>
        </div>
      </div>
    </div>
  );
}

export default Mappage;
