import React, { useContext, useEffect, useState } from "react";
import "./mapstyle.css";
import axios from "axios";
import { FaCartArrowDown } from "react-icons/fa";
import { CiShoppingCart } from "react-icons/ci";
import { Link, useLocation } from "react-router-dom";
import { cartItemcontext, cartStatecontext, datacontext } from "../App";

function Mappage() {
  const [data, setdata] = useContext(datacontext);
  const [cartStates, setCartStates] = useContext(cartStatecontext);
  const [cartitem, setcartitem] = useContext(cartItemcontext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("https://fakestoreapi.com/products");
        setdata(response.data);
        setCartStates(new Array(response.data.length).fill(false));
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const addtocartt = (index) => {
    const updatedCartStates = [...cartStates];
    updatedCartStates[index] = !updatedCartStates[index];
    setCartStates(updatedCartStates);
  };

  useEffect(() => {
    const cartItems = data.filter((_, index) => cartStates[index]);
    setcartitem(cartItems);
  }, [cartStates, data]);
  // console.log(cartitem);

  return (
    <div className="main-container">
      <div className="container">
        <div className="text">
          <h2>Check out our products</h2>
        </div>
        {/* <div className="card-map">
          {data.map((obj, index) => (
            <div className="card-container" key={index}>
              <div className="top">
                <img src={obj.image} alt="" />
              </div>
              {cartStates[index] ? (
                <h1>added to cart</h1>
              ) : (
                <>
                  <div className="bottom">
                    <div className="left">
                      <div className="contents">
                        <h2>{obj.category}</h2>
                        <h3>{obj.price}</h3>
                      </div>
                    </div>
                    <div className="right">
                      <button
                        onClick={() => addtocartt(index)}
                        disabled={cartStates[index]}
                      >
                        <FaCartArrowDown className="cartbutton" />
                      </button>
                    </div>
                  </div>
                </>
              )}
            </div>
          ))}
        </div> */}

        <div className="card-map">
          {data.map((obj, index) => (
            <>
              <div className="card-container">
                <div className="top">
                  <img
                    src="https://th.bing.com/th/id/OIP.H6Vz9zpi9tCZZe1rjvNalQHaL2?rs=1&pid=ImgDetMain"
                    alt=""
                  />
                </div>
                <div className="bottom">
                  <div className="brandname"></div>
                  <div className="productname"></div>
                  <div className="price"></div>

                  .but
                </div>
              </div>
            </>
          ))}
        </div>
      </div>

      {/* <Link to="./cart" className="cart-icon">
          <CiShoppingCart className="pinkcart" />
        </Link> */}

      {/* </div> */}
    </div>
  );
}

export default Mappage;
